import ScrollMenu from 'react-horizontal-scrolling-menu' // ScrollMenu 가져오기
import React, { useState, useEffect } from 'react';
let list = [
    { name: '2323' },
    { name: '23' },
    { name: '1111' },
    { name: '4444' }
] // 임시데이터 선언

// Item 단위의 컴포넌트
const MenuItem = ({ text }) => {
    return <div className="menu-item">{text}</div>;
};

// MenuItem들을 list만큼 생성한 List단위의 컴포넌트
export const Menu = list => (
    list.map(el => {
        return <MenuItem text={el.name} key={el.name} />
    })
);

function Selector() {

    const [DataItem, setDataItem] = useState(null);
    useEffect(() => {
        setDataItem(Menu(list)); // Menu컴포넌트를 state에 저장
    }, [])


    return (
        <>
            {DataItem}

            {
                DataItem &&
                <ScrollMenu>
                    {list.map((el) => {
                        return <MenuItem text={el.name} key={el.name} />
                    })}
                </ScrollMenu>
            }
        </>
    );
}

export default Selector;
