import ScrollMenu from 'react-horizontal-scrolling-menu' // ScrollMenu 가져오기
import React, { useState, useEffect } from 'react';
let list = [
    { name: '2323' },
    { name: '23' },
    { name: '1111' },
    { name: '4444' }
] // 임시데이터 선언

// Item 단위의 컴포넌트
const MenuItem = ({ text }) => {
    return <div className="menu-item">{text} {text}</div>;
};

// MenuItem들을 list만큼 생성한 List단위의 컴포넌트
export const Menu = (list) => (
    list.map(el => {
        return <MenuItem text={el.name} key={el.name} />
    })
);

function LeftArrow() {
    const { isFirstItemVisible, scrollPrev } =
      React.useContext(VisibilityContext);

    return (
      <Arrow disabled={isFirstItemVisible} onClick={() => scrollPrev()}>
        Left
      </Arrow>
    );
  }

  function RightArrow() {
    const { isLastItemVisible, scrollNext } = React.useContext(VisibilityContext);

    return (
      <Arrow disabled={isLastItemVisible} onClick={() => scrollNext()}>
        Right
      </Arrow>
    );
  }

function Selector() {

    const [DataItem, setDataItem] = useState(null);
    useEffect(() => {
        setDataItem(Menu(list)); // Menu컴포넌트를 state에 저장
    }, [])


    return (
        <>
            {
                DataItem &&
                <ScrollMenu  LeftArrow={LeftArrow} RightArrow={RightArrow}>{DataItem}
                </ScrollMenu>
            }
        </>
    );
}

export default Selector;
